import InAndDe from "./InAndDe.vue";
export default {
  props: {
    shop_cart_list: {
      type: Array
    },
    show_cart: {
      type: Boolean
    },
    winH: {
      type: Number
    }
  },
  data: function data() {
    return {};
  },
  methods: {
    //清空购物车
    clear_cart: function clear_cart() {
      this.$store.dispatch('clearCart');
    },
    show_shopCart: function show_shopCart() {
      this.$store.dispatch('show_cart');
    },
    hideCart: function hideCart() {
      this.$store.dispatch('show_cart');
    }
  },
  components: {
    InAndDe: InAndDe
  },
  computed: {
    //计算购物车商品总价
    total_price: function total_price() {
      var sum = 0;
      this.shop_cart_list.forEach(function (item, index) {
        sum += item.count * item.price;
      });
      return sum;
    },
    //计算购物车内容的总长度
    goods_count: function goods_count() {
      return this.shop_cart_list.length;
    },
    //计算遮罩mask的高度和bottom值
    maskHB: function maskHB() {
      var num = this.shop_cart_list.length > 3 ? 3 : this.shop_cart_list.length;
      var height = this.winH - 134 - num * 40 - 40 + 'px';
      var bottom = num * 40 + 40 + 'px';
      return {
        h: height,
        b: bottom
      };
    }
  }
};
