export default {
  data: function data() {
    return {
      show_login: false,
      phone: '',
      captcha: '',
      dis: true,
      test_cap: false,
      test_cap_con: '获取验证码',
      location_home: '',
      timer: null
    };
  },
  methods: {
    //跳转订单组件
    showbill: function showbill(con) {
      this.$router.push({
        path: "/bill",
        query: {
          con: con
        }
      });
    },
    //显示登陆块
    login: function login() {
      this.show_login = true;
    },
    //隐藏登陆块
    back: function back() {
      this.show_login = false;
    },
    jianting: function jianting(v) {
      if (v.length >= 6) {
        this.dis = false;
      }
    },
    haha: function haha() {
      this.$Toast('测试短信验证码哦,不能真的登陆');
      this.dis = true;
      this.phone = '';
      this.captcha = '';
      clearInterval(this.timer);
      this.test_cap_con = '获取验证码';
    },
    test_location: function test_location(v) {
      if (v.length == 11) {
        var _this = this;

        var location_url = 'http://apis.juhe.cn/mobile/get?phone=' + this.phone + '&key=f426ea535a15ca34ad69de80c2afdf05'; //验证手机号归属地接口

        $.ajax({
          type: 'get',
          url: location_url,
          dataType: 'jsonp',
          success: function success(res) {
            _this.location_home = res.result.city + res.result.company;
          }
        });
      } else {
        this.location_home = '';
      }
    },
    //发送验证码
    test_captcha: function test_captcha() {
      var _this = this;

      var result = this.isPoneAvailable(this.phone);

      if (result) {
        var code_url = 'http://v.juhe.cn/sms/send?mobile=' + this.phone + '&tpl_id=159959&tpl_value=%23code%23%3d431515&dtype=json&key=9224c0f358a9a6efbca99b415e8f80dc%20'; //发送验证码接口

        $.ajax({
          type: 'get',
          url: code_url,
          dataType: 'jsonp',
          success: function success(res) {
            console.log(res);
          }
        });
        this.test_cap = true;
        var num = 60;
        this.test_cap_con = num + '秒后重新获取';
        this.timer = setInterval(function () {
          num--;
          _this.test_cap_con = num + '秒后重新获取';

          if (num <= 0) {
            clearInterval(_this.timer);
            _this.test_cap = false;
            _this.test_cap_con = '获取验证码';
          }
        }, 1000);
      } else {
        this.$Toast('请输入正确11位手机号');
      }
    },
    //验证手机号正则
    isPoneAvailable: function isPoneAvailable(phone) {
      var myreg = /^[1][3,4,5,7,8][0-9]{9}$/;

      if (!myreg.test(phone)) {
        return false;
      } else {
        return true;
      }
    }
  }
};
