export default {
  props: {
    movies: {
      type: Array,
      default: []
    },
    cinemaName: {
      type: String,
      default: ""
    }
  },
  data: function data() {
    return {
      winH: 0
    };
  },
  mounted: function mounted() {
    this.winH = document.documentElement.clientHeight;
  },
  methods: {
    //获取url路径上的cityName参数值
    GetQueryString: function GetQueryString(name) {
      var reg = new RegExp("(^|&)" + name + "=([^&]*)(&|$)+");
      var r = window.location.href.split("?")[1] ? window.location.href.split("?")[1].match(reg) : null; //decodeURIComponent函数编码的 URI 进行解码

      if (r != null) return decodeURIComponent(r[2]);
      return null;
    },
    setH: function setH() {
      //数据获取过后设置电影列表的高度,
      this.$refs.movie_list_dom.style.height = document.documentElement.clientHeight - 187 + "px";
    }
  },
  watch: {
    $route: function $route(old, NewVal) {
      //路由改变时改变电影院内容
      var cityName = this.GetQueryString("cinemaName");

      if (cityName) {
        this.$store.dispatch("change_cinemaName", cityName);
      }
    }
  }
};
