.goods_list{
  display:flex;
  background:#fff url(../../../public/image/goods_bg.jpg) top left repeat-x;
  background-size:auto 30px;
}
.goods_list_nav{
  flex-grow:1;
  height:100%;
  overflow:auto;
  background:#fff url(../../../public/image/goods_bg.jpg) top left repeat-x;
  background:#fdfbee url(/image/goods_bg.jpg) top left repeat-x;
  background-size:auto 30px;
  width:90px;
}
.goods_list_nav h6{
  margin:0;
  line-height:36px;
  border-bottom: 1px solid #ccc;
}
.goods_list h6:nth-child(1){
  margin-top: 30px;
}
.goods_list_nav h6.active{
  background:#fff; 
}
.goods_list_con{
  flex-grow:4;
  height:100%;
  overflow:auto;
  width:calc( 100% - 90px );
}
.goods_list_con .list{
  border-top:1px solid #ccc;
}
.goods_list_con h6{
  margin-bottom: 0;
  height: 36px;
  background:url(/image/goods_bg2.jpg) top center no-repeat;
  background-size:auto 36px;
  line-height:36px;
}
.goods_list_con dl{
  display:flex;
  text-align:left;
}
.goods_list_con dl dt{
  width:70px;
}
.goods_list_con dl dt img{
  width:60px;
  margin:5px;
  margin-top:0;
}
.goods_list_con dl dd{
   margin-left:0
}
.goods_list_con .goods_list_des{
  width:calc( 100% - 106px );
}
.goods_list_con .goods_list_des *{
  display:block;
  margin-bottom:3px;
}
.goods_list_con .goods_list_des span{
  font-size:12px;
}
.goods_list_con .goods_list_des i{
  color:#e0b17d;
  font-weight:700;
}
.goods_list_con .goods_list_add_shopcart{
  padding-top:30px;
}
