#shop_cart{
    height:45px;
    border-top:1px solid #ccc;
    border-bottom:1px solid #ccc;
    position:relative;
    display:flex;
}
#shop_cart .shopCart{
    flex-grow:2;
}
#shop_cart .shopCart .cart{
    width:45px;
    height:45px;
    background:#747a88;
    border-radius:50%;
    color:#fff;
    text-align:center;
    line-height:45px;
    position:relative;
    float:left;
    margin: -10px 10px 0 10px;
    z-index:2;
}
#shop_cart .shopCart .cart i{
    font-size:20px;
}
#shop_cart .shopCart .cart b{
    width:15px;
    height:15px;
    background:#ddab78;
    border-radius:50%;
    text-align:center;
    line-height:15px;
    position:absolute;
    right: -2px;
    top: -3px;
    font-size:12px;
}
#shop_cart .shopCart .price{
    color:#ddab78;
    font-size:25px;
    font-weight:bold;
    float: left;
    line-height: 45px;
}
#shop_cart .calculate{
    flex-grow:1;
}
#shop_cart .calculate .calculate_btn{
    width:100%;
    height:100%;
    background:#ddab78;
    color:#fff;
    text-align:center;
    font-size:18px;
    border:0;
}
#shop_cart .shopCart_list{
    position:absolute;
    bottom:47px;
    width:100%;
    background:#fff;
}
#shop_cart .shopCart_list .mask{
    position:absolute;
    width:100%;
    background:rgba(0,0,0,0.3);
}
#shop_cart .shopCart_list .shopCart_list_tit{
    height:40px;
    background:#e8eaf0;
    overflow:hidden;
    line-height:40px;
    color:#9a9999;
}
#shop_cart .shopCart_list .shopCart_list_tit .maipin{
    float:left;
    font-size:15px;
    margin-left:5px;
}
#shop_cart .shopCart_list .shopCart_list_tit .clearShopCart{
    float:right;
    font-size:12px;
    margin-right:5px;
}
#shop_cart .shopCart_list .ShopCart_list_con{
    display:flex;
    height:40px;
    line-height:40px;
}
#shop_cart .shopCart_list .ShopCart_list_con .goods_name{
    width:50vw;
    text-align:left;
    padding-left:5px;
}
#shop_cart .shopCart_list .ShopCart_list_con .goods_price{
    width:25vw;
}
