#movie_list {
  display: flex;
  flex-direction: column;
}
.carousel {
  width: 100%;
  height: 100px;
}
.carousel img {
  width: 100%;
}
// .movies_list{
//   height:100%;
// }
.movies_list > div {
  display: flex;
  border-bottom: 1px solid #ccc;
  padding: 0 5px;
}
.movies_list > div > div {
  flex-grow: 1;
  text-align: left;
}
.movies_list .movies_list_img{
  text-align:center;
}
.movies_list > div img {
  width: 60px;
  margin: 10px;
}
.movies_list .movies_list_name{
  width:25vw;
}
.movies_list .movies_list_name h6{
  margin: 15px 0px;
  font-size:14px;
}
.movies_list .movies_list_name span{
  font-size:13px;
}
.movies_list > div p {
  margin-top: 15px;
  margin-bottom: 8px;
  text-indent: 5px;
}
.mint-button{
  border-radius:10px;
}
